google.setOnLoadCallback(function(){$("div.slider").each(function(a,b){$(b).slider({min:0,max:4,step:.5,value:$(this).data("value"),change:function(a,b){var c=$(this),d=c.data("did"),e=$(".section").data("sid"),f=c.slider("value").toString(),g=c.parents("form").serialize();g+="&mark[nummark]="+f,$('.mark[data-did="'+d+'"]').text("..."),$.post("/sections/"+e+"/mark",g,function(a){c.data("mid",a),$('.mark[data-did="'+d+'"]').text(f)})}})})});